// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.9;

contract VotingAndFunds {
    address payable public owner;
    uint256 public balance;

    event Deposit(uint256 amount);
    event Withdraw(uint256 amount);
    event VoteCasted(address indexed voter, uint8 candidate);
    event LoanTaken(address indexed borrower, uint256 amount, uint256 interest);
    event LoanRepaid(address indexed borrower, uint256 amount);

    struct Voter {
        bool voted;
        uint8 vote;
    }

    mapping(address => Voter) public voters;
    mapping(uint8 => uint256) public votes;
    mapping(address => uint256) public loans;

    uint256 public constant INTEREST_RATE = 5;

    constructor() payable {
        owner = payable(msg.sender);
        balance = msg.value;
    }

    function getBalance() public view returns (uint256) {
        return balance;
    }

    error InsufficientBalance(uint256 balance, uint256 withdrawAmount);

    function deposit() public payable {
        require(msg.value > 0, "Deposit amount must be greater than 0");
        balance += msg.value;
        emit Deposit(msg.value);
    }

    function withdraw(uint256 _withdrawAmount) public {
        require(msg.sender == owner, "You are not the owner of this account");
        if (balance < _withdrawAmount) {
            revert InsufficientBalance(balance, _withdrawAmount);
        }
        balance -= _withdrawAmount;
        payable(msg.sender).transfer(_withdrawAmount);
        emit Withdraw(_withdrawAmount);
    }

    function vote(uint8 _candidate) public {
        require(!voters[msg.sender].voted, "You have already voted.");
        voters[msg.sender] = Voter(true, _candidate);
        votes[_candidate] += 1;
        emit VoteCasted(msg.sender, _candidate);
    }

    function getVotes(uint8 _candidate) public view returns (uint256) {
        return votes[_candidate];
    }

    function takeLoan(uint256 _amount) public {
        require(_amount > 0, "Loan amount must be greater than 0");
        require(_amount <= balance, "Not enough funds in the contract");
        uint256 interest = (_amount * INTEREST_RATE) / 100;
        loans[msg.sender] = _amount + interest;
        balance -= _amount;
        payable(msg.sender).transfer(_amount);
        emit LoanTaken(msg.sender, _amount, interest);
    }

    function repayLoan() public payable {
        require(loans[msg.sender] > 0, "You have no loans to repay");
        require(msg.value == loans[msg.sender], "Incorrect repayment amount");

        loans[msg.sender] = 0;
        balance += msg.value;
        emit LoanRepaid(msg.sender, msg.value);
    }
}

